class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }


  componentDidMount() {
    fetch(url)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    return (
      <div className="App">
      <BarChart
      data={[
        {x: 'A', y: 20},
        {x: 'B', y: 30},
        {x: 'C', y: 40},
        {x: 'D', y: 20},
        {x: 'E', y: 40},
        {x: 'F', y: 25},
        {x: 'G', y: 5}
      ]}
      />
      <p>My Token = {window.token}</p>
      <ul>
          {items.map(item => (
            <li key={item.name}>
              {item.name} {item.price}
            </li>
          ))}
        </ul>
      </div>
    )
  }
}

export default App;
